import React from 'react';
import telaImage from '../../assets/tela.jpg';
import topoImage from '../../assets/topo.jpg';
import faixaDirImage from '../../assets/faixaDir.jpg';
import ladoEsqTecImage from '../../assets/ladoEsqTec.jpg';
import bateriaImage from '../../assets/bateria.jpg';
import ptabaixo7Image from '../../assets/ptabaixo7.jpg';
import confirmaImage from '../../assets/confirma.jpg';
import brancoImage from '../../assets/branco.jpg';
import corrigeImage from '../../assets/corrige.jpg';
import abaixoTecImage from '../../assets/abaixoTec.jpg';
import ptAbaixo9Image from '../../assets/ptabaixo9.jpg';
import "./styles.css";

export function Urna() {
    const buttonImages = {
        n0: {
            normal: '../../assets/n0.jpg',
            pressed: '../../assets/n0_down.jpg'
        },
        n1: {
            normal: '../../assets/n1.jpg',
            pressed: '../../assets/n1_down.jpg'
        },
        n2: {
            normal: '../../assets/n2.jpg',
            pressed: '../../assets/n2_down.jpg'
        },
        n3: {
            normal: '../../assets/n3.jpg',
            pressed: '../../assets/n3_down.jpg'
        },
        n4: {
            normal: '../../assets/n4.jpg',
            pressed: '../../assets/n4_down.jpg'
        },
        n5: {
            normal: '../../assets/n5.jpg',
            pressed: '../../assets/n5_down.jpg'
        },
        n6: {
            normal: '../../assets/n6.jpg',
            pressed: '../../assets/n6_down.jpg'
        },
        n7: {
            normal: '../../assets/n7.jpg',
            pressed: '../../assets/n7_down.jpg'
        },
        n8: {
            normal: '../../assets/n8.jpg',
            pressed: '../../assets/n8_down.jpg'
        },
        n9: {
            normal: '../../assets/n9.jpg',
            pressed: '../../assets/n9_down.jpg'
        },
        confirma: {
            normal: '../../assets/confirma.jpg',
            pressed: '../../assets/confirma_down.jpg'
        },
        branco: {
            normal: '../../assets/branco.jpg',
            pressed: '../../assets/branco_down.jpg'
        },
        corrige: {
            normal: '../../assets/corrige.jpg',
            pressed: '../../assets/corrige_down.jpg'
        }
    };

    const [buttonState, setButtonState] = React.useState(
        Object.fromEntries(
            Object.keys(buttonImages).map((buttonId) => [
                buttonId,
                buttonImages[buttonId].normal
            ])
        )
    );

    function updateButtonState(buttonId) {
        setTimeout(() => {
            setButtonState((prevState) => ({
                ...prevState,
                [buttonId]: buttonImages[buttonId].normal
            }));
        }, 200);
        setButtonState((prevState) => ({
            ...prevState,
            [buttonId]: buttonImages[buttonId].pressed
        }));
    }

    function renderButtons() {
        return Object.entries(buttonImages).map(([buttonId, buttonData]) => (
            <img
                key={buttonId}
                id={buttonId}
                alt={buttonId}
                src={buttonState[buttonId]}
                width="..."
                height="..."
                border="0"
                onClick={() => updateButtonState(buttonId)}
            />
        ));
    }

    return (
        <div id="conteudo">
            <div className="barra-topo">&nbsp;</div>
            <img className="tela" src={telaImage} alt="Tela" />
            <img className="topo" src={topoImage} alt="Topo" />
            <img className="faixaDir" src={faixaDirImage} alt="Faixa Direita" />
            <img className="ladoEsqTec" src={ladoEsqTecImage} alt="Lado Esquerdo Técnico" />
            <img className="bateria" src={bateriaImage} alt="Bateria" />
            <img className="ptabaixo7" src={ptabaixo7Image} alt="Ponta Baixo 7" />
            <img className="confirma" src={confirmaImage} alt="Confirma" />
            <img className="branco" src={brancoImage} alt="Branco" />
            <img className="corrige" src={corrigeImage} alt="Corrige" />
            <img className="abaixoTec" src={abaixoTecImage} alt="Abaixo Técnico" />
            <img className="ptabaixo9" src={ptAbaixo9Image} alt="Ponta Baixo 9" />
            {renderButtons()}
        </div>
    );
}
